import React, { useRef } from 'react';

const SaveWall = ({ onSave, onDelete, onUpload }) => {
  const fileInputRef = useRef();

  const handleUploadClick = () => {
    fileInputRef.current.click();
  };

  return (
    <div style={{ display: 'flex', flexDirection: 'column', gap: '10px', marginTop: '10px', alignItems: 'center' }}>
      <button
        style={{ background: '#2a509c', color: 'white', fontWeight: 'bold', padding: '10px 24px', borderRadius: 6, fontSize: 18, width: 200 }}
        onClick={handleUploadClick}
      >
        Upload Image
      </button>
      <input
        type="file"
        accept="image/*"
        multiple
        ref={fileInputRef}
        onChange={onUpload}
        style={{ display: 'none' }}
      />
      <button
        style={{ background: '#2a509c', color: 'white', fontWeight: 'bold', padding: '10px 24px', borderRadius: 6, fontSize: 18, width: 200 }}
        onClick={onSave}
      >
        Save Wall
      </button>
      <button
        style={{ background: '#2a509c', color: 'white', fontWeight: 'bold', padding: '10px 24px', borderRadius: 6, fontSize: 18, width: 200 }}
        onClick={onDelete}
      >
        Delete Selected Image
      </button>
    </div>
  );
};

export default SaveWall;
