import React, { useState } from 'react';
import Wall from './Wall';
import './Wall.css';

const defaultWalls = [
  '/walls/wall1.jpg',
  '/walls/wall2.jpg',
  '/walls/wall3.jpg',
  '/walls/wall4.jpg',
  '/walls/wall5.jpg',
  '/walls/wall6.jpg',
];

function App() {
  // Wall background state
  const [selectedType, setSelectedType] = useState('image');
  const [selectedWall, setSelectedWall] = useState(defaultWalls[0]);
  const [selectedColor, setSelectedColor] = useState('#ffffff');
  const [uploadedWall, setUploadedWall] = useState(null);
  const [width, setWidth] = useState(800);
  const [height, setHeight] = useState(500);

  // Handle wall background selection
  const handleWallClick = (wall) => {
    setSelectedType('image');
    setSelectedWall(wall);
    setUploadedWall(null);
  };
  const handleColorChange = (e) => {
    setSelectedType('color');
    setSelectedColor(e.target.value);
    setSelectedWall(null);
    setUploadedWall(null);
  };
  const handleUpload = (e) => {
    const file = e.target.files[0];
    if (file) {
      setSelectedType('upload');
      setUploadedWall(URL.createObjectURL(file));
      setSelectedWall(null);
    }
  };

  // Compose wallBg prop for Wall
  let wallBg = null;
  if (selectedType === 'image') wallBg = { type: 'image', value: selectedWall };
  else if (selectedType === 'color') wallBg = { type: 'color', value: selectedColor };
  else if (selectedType === 'upload') wallBg = { type: 'upload', value: uploadedWall };

  return (
    <div className="main-layout" style={{ flexDirection: 'row', alignItems: 'flex-start', justifyContent: 'center', minHeight: '100vh', gap: 40 }}>
      {/* Left: Wall background selection and preview */}
      <div style={{ display: 'flex', flexDirection: 'column', alignItems: 'center', minWidth: 320 }}>
        <h1 style={{ fontSize: 36, color: '#2a639c', marginBottom: 16 }}>Wall Poster</h1>
        <h3>Choose a wall background</h3>
        <div style={{ display: 'flex', flexWrap: 'wrap', gap: 12, justifyContent: 'center', marginBottom: 12 }}>
          {defaultWalls.map((wall, idx) => (
            <img
              key={wall}
              src={wall}
              alt={`Wall ${idx + 1}`}
              style={{
                width: 80,
                height: 56,
                objectFit: 'cover',
                border: selectedType === 'image' && selectedWall === wall ? '3px solid #2a509c' : '2px solid #ccc',
                borderRadius: 8,
                cursor: 'pointer',
              }}
              onClick={() => handleWallClick(wall)}
            />
          ))}
        </div>
        {/* Upload custom wall background */}
        <div style={{ marginBottom: 12 }}>
          <input type="file" accept="image/*" onChange={handleUpload} id="wall-upload" style={{ display: 'none' }} />
          <label htmlFor="wall-upload" style={{ background: '#2a509c', color: 'white', padding: '6px 16px', borderRadius: 6, cursor: 'pointer', fontWeight: 'bold' }}>
            Upload Custom Wall
          </label>
          {uploadedWall && (
            <img src={uploadedWall} alt="Uploaded Wall" style={{ width: 80, height: 56, objectFit: 'cover', borderRadius: 8, marginLeft: 12, border: selectedType === 'upload' ? '3px solid #2a509c' : '2px solid #ccc', cursor: 'pointer' }} onClick={() => setSelectedType('upload')} />
          )}
        </div>
        {/* Color picker */}
        <div style={{ marginBottom: 12, display: 'flex', alignItems: 'center', gap: 8 }}>
          <input type="color" value={selectedColor} onChange={handleColorChange} style={{ width: 36, height: 36, border: 'none', background: 'none', cursor: 'pointer' }} />
          <span>Choose background color</span>
        </div>
        {/* Wall size controls */}
        <div style={{ marginBottom: 16, display: 'flex', gap: 12 }}>
          <label>
            Width:
            <input type="number" value={width} min={200} max={2000} onChange={e => setWidth(Number(e.target.value))} style={{ marginLeft: 4, width: 60 }} />
          </label>
          <label>
            Height:
            <input type="number" value={height} min={200} max={2000} onChange={e => setHeight(Number(e.target.value))} style={{ marginLeft: 4, width: 60 }} />
          </label>
        </div>
        {/* Wall preview */}
        <div style={{ width: width, height: height, border: '3px solid #2a509c', borderRadius: 12, backgroundSize: 'cover', backgroundPosition: 'center', backgroundColor: selectedType === 'color' ? selectedColor : '#f3f3f3', backgroundImage: selectedType === 'image' ? `url('${selectedWall}')` : selectedType === 'upload' ? `url('${uploadedWall}')` : 'none', display: 'flex', alignItems: 'center', justifyContent: 'center', marginBottom: 16 }}>
          {/* The actual wall preview is handled in Wall component, this is just a background preview */}
        </div>
      </div>
      {/* Right: Wall editor and controls */}
      <div style={{ flex: 1, minWidth: 400 }}>
        <Wall wallBg={wallBg} wallSize={{ width, height }} />
      </div>
    </div>
  );
}

export default App;
