import React, { useState } from 'react';
import { Rnd } from 'react-rnd';
import './Wall.css';
import SaveWall from './SaveWall';
import html2canvas from 'html2canvas';

const Wall = ({ wallBg, wallSize }) => {
  // Uploaded image state
  const [images, setImages] = useState([]);
  // State for context menu
  const [contextMenu, setContextMenu] = useState({ visible: false, x: 0, y: 0, imgId: null });
  // Add selectedImgId state
  const [selectedImgId, setSelectedImgId] = useState(null);
  const wallRef = React.useRef(null);

  // Handle image file input
  const handleImageUpload = (e) => {
    const files = Array.from(e.target.files);
    const newImages = files.map((file, index) => ({
      id: Date.now() + index, // unique ID
      src: URL.createObjectURL(file),
      x: 0,
      y: 0,
      width: 150,
      height: 150,
    }));
    setImages((prev) => [...prev, ...newImages]);
  };

  // Update handleContextMenu to set selectedImgId
  const handleContextMenu = (e, imgId) => {
    e.preventDefault();
    setContextMenu({ visible: true, x: e.clientX, y: e.clientY, imgId });
    setSelectedImgId(imgId);
  };

  // New handler for SaveWall delete button
  const handleDeleteSelected = () => {
    if (selectedImgId !== null) {
      setImages((prev) => prev.filter((img) => img.id !== selectedImgId));
      setSelectedImgId(null);
      setContextMenu((cm) => ({ ...cm, visible: false }));
    }
  };

  // Hide context menu on click elsewhere
  React.useEffect(() => {
    const handleClick = () => setContextMenu((cm) => ({ ...cm, visible: false }));
    if (contextMenu.visible) {
      window.addEventListener('click', handleClick);
    }
    return () => window.removeEventListener('click', handleClick);
  }, [contextMenu.visible]);

  // Save wall as image
  const saveWall = async () => {
    if (!wallRef.current) return;
    const canvas = await html2canvas(wallRef.current);
    const link = document.createElement('a');
    link.download = 'custom-wall.png';
    link.href = canvas.toDataURL('image/png');
    link.click();
  };

  return (
    <div className="main-layout">
      <div style={{ width: '100%' }}>
        <h1 style={{ fontSize: 48, color: '#2a639c', marginBottom: 24, textAlign: 'center' }}>Wall Poster</h1>
      </div>
      {/* Wall Section - placed on the left */}
      <div
        className="custom-wall"
        ref={wallRef}
        style={{
          backgroundColor: wallBg && wallBg.type === 'color' ? wallBg.value : undefined,
          backgroundImage: wallBg && wallBg.type !== 'color' ? `url('${wallBg.value}')` : undefined,
          backgroundSize: 'cover',
          backgroundPosition: 'center',
          width: wallSize.width + 'px',
          height: wallSize.height + 'px',
          position: 'relative',
        }}
      >
        {/* Display uploaded image if present */}
        {images.map((img) => (
          <Rnd
            key={img.id}
            default={{
              x: img.x,
              y: img.y,
              width: img.width,
              height: img.height,
            }}
            bounds="parent"
            minWidth={50}
            minHeight={50}
          >
            <img
              src={img.src}
              alt="user-img"
              style={{
                width: '100%',
                height: '100%',
                objectFit: 'cover',
                border: '2px solid #000',
              }}
              onContextMenu={(e) => handleContextMenu(e, img.id)}
            />
          </Rnd>
        ))}
      </div>
      {/* Controls Section - placed on the right */}
      <div className="controls">
        {/* Upload Image Button */}
        <button className="upload-image-btn" onClick={() => document.getElementById('uploadInput').click()}>
          Upload Image
        </button>
        {/* Hidden file input - triggered by button */}
        <input
          type="file"
          id="uploadInput"
          accept="image/*"
          multiple
          onChange={handleImageUpload}
          style={{ display: 'none' }}
        />
        {/* Save Wall Button */}
        <SaveWall onSave={saveWall} onDelete={handleDeleteSelected} />
      </div>
    </div>
  );
};

export default Wall;
