import React, { useState } from 'react';
import { Rnd } from 'react-rnd';
import './Wall.css';
import SaveWall from './SaveWall';
import html2canvas from 'html2canvas';
import ShapeSelector from './ShapeSelector';

const Wall = ({ wallBg, wallSize }) => {
  // Uploaded image state, now with shape and zoom
  const [images, setImages] = useState([]);
  const [selectedImgId, setSelectedImgId] = useState(null);
  const [showShapeSelector, setShowShapeSelector] = useState(false);
  const wallRef = React.useRef(null);

  // Handle image file input
  const handleImageUpload = (e) => {
    const files = Array.from(e.target.files);
    const newImages = files.map((file, index) => ({
      id: Date.now() + index, // unique ID
      src: URL.createObjectURL(file),
      x: 0,
      y: 0,
      width: 150,
      height: 150,
      shape: 'rectangle',
      zoom: 1,
    }));
    setImages((prev) => [...prev, ...newImages]);
  };

  // Handle double click to select image
  const handleSelectImage = (imgId) => {
    setSelectedImgId(imgId);
    setShowShapeSelector(false);
  };

  // Handle zoom with mouse wheel
  const handleWheel = (e, imgId) => {
    if (selectedImgId !== imgId) return;
    e.preventDefault();
    setImages((prev) =>
      prev.map((img) =>
        img.id === imgId
          ? { ...img, zoom: Math.max(0.5, Math.min(2, img.zoom + (e.deltaY < 0 ? 0.05 : -0.05))) }
          : img
      )
    );
  };

  // Handle shape change
  const handleShapeChange = (shape) => {
    setImages((prev) =>
      prev.map((img) =>
        img.id === selectedImgId ? { ...img, shape } : img
      )
    );
    setShowShapeSelector(false);
  };

  // Handle zoom change from ShapeSelector (for preview)
  const handleZoomChange = (zoom) => {
    setImages((prev) =>
      prev.map((img) =>
        img.id === selectedImgId ? { ...img, zoom } : img
      )
    );
  };

  // Delete selected image
  const handleDeleteSelected = () => {
    if (selectedImgId !== null) {
      setImages((prev) => prev.filter((img) => img.id !== selectedImgId));
      setSelectedImgId(null);
    }
  };

  // Save wall as image
  const saveWall = async () => {
    if (!wallRef.current) return;
    const canvas = await html2canvas(wallRef.current);
    const link = document.createElement('a');
    link.download = 'custom-wall.png';
    link.href = canvas.toDataURL('image/png');
    link.click();
  };

  // Get selected image object
  const selectedImg = images.find((img) => img.id === selectedImgId);

  // Shape mask styles
  const getMaskStyle = (img) => {
    switch (img.shape) {
      case 'circle':
        return { borderRadius: '50%' };
      case 'square':
        return { aspectRatio: '1 / 1', borderRadius: 8 };
      case 'triangle':
        return {
          clipPath: 'polygon(50% 0%, 0% 100%, 100% 100%)',
        };
      case 'heart':
        return {
          clipPath:
            'polygon(50% 90%, 100% 60%, 85% 30%, 70% 20%, 50% 35%, 30% 20%, 15% 30%, 0% 60%, 50% 90%)',
        };
      default:
        return { borderRadius: 8 };
    }
  };

  return (
    <div className="main-layout" style={{ flexDirection: 'row', alignItems: 'center', justifyContent: 'center', minHeight: '100vh', position: 'relative', paddingTop: 120 }}>
      <div style={{ position: 'absolute', top: 32, left: 0, width: '100%' }}>
        <h1 style={{ fontSize: 48, color: '#2a639c', marginBottom: 24, textAlign: 'center' }}>Wall Poster</h1>
      </div>
      <div style={{ flex: '0 0 auto', display: 'flex', flexDirection: 'column', alignItems: 'center', justifyContent: 'center', height: '100vh' }}>
        <div
          className="custom-wall"
          ref={wallRef}
          style={{
            backgroundColor: wallBg && wallBg.type === 'color' ? wallBg.value : undefined,
            backgroundImage: wallBg && wallBg.type !== 'color' ? `url('${wallBg.value}')` : undefined,
            backgroundSize: 'cover',
            backgroundPosition: 'center',
            width: wallSize.width + 'px',
            height: wallSize.height + 'px',
            position: 'relative',
            margin: '0 auto',
            display: 'flex',
            alignItems: 'center',
            justifyContent: 'center',
          }}
        >
          {/* Display uploaded image if present */}
          {images.map((img) => (
            <Rnd
              key={img.id}
              default={{
                x: img.x,
                y: img.y,
                width: img.width,
                height: img.height,
              }}
              bounds="parent"
              minWidth={50}
              minHeight={50}
            >
              <img
                src={img.src}
                alt="user-img"
                style={{
                  width: `${img.zoom * 100}%`,
                  height: `${img.zoom * 100}%`,
                  objectFit: 'cover',
                  border: selectedImgId === img.id ? '2px solid #2a509c' : '2px solid #000',
                  boxShadow: selectedImgId === img.id ? '0 0 8px #2a509c' : 'none',
                  cursor: 'pointer',
                  transition: 'all 0.2s',
                  ...getMaskStyle(img),
                }}
                onDoubleClick={() => handleSelectImage(img.id)}
                onWheel={(e) => handleWheel(e, img.id)}
              />
            </Rnd>
          ))}
          {/* Shape selector popup */}
          {selectedImg && showShapeSelector && (
            <div style={{ position: 'absolute', left: '50%', top: '50%', transform: 'translate(-50%, -50%)', zIndex: 20, background: '#232526', borderRadius: 12, padding: 24, boxShadow: '0 4px 24px #0008' }}>
              <ShapeSelector
                image={selectedImg.src}
                shape={selectedImg.shape}
                zoom={selectedImg.zoom}
                onShapeChange={handleShapeChange}
                onZoomChange={handleZoomChange}
              />
              <button style={{ marginTop: 16, background: '#2a509c', color: 'white', fontWeight: 'bold', padding: '8px 24px', borderRadius: 6, fontSize: 16 }} onClick={() => setShowShapeSelector(false)}>
                Close
              </button>
            </div>
          )}
        </div>
      </div>
      {/* Controls Section - placed on the right */}
      <div className="controls" style={{ position: 'fixed', right: 40, top: '50%', transform: 'translateY(-50%)', display: 'flex', flexDirection: 'column', gap: 24, alignItems: 'center', zIndex: 10 }}>
        <button
          style={{ background: '#2a509c', color: 'white', fontWeight: 'bold', padding: '10px 24px', borderRadius: 6, fontSize: 18, width: 200, opacity: selectedImg ? 1 : 0.5, cursor: selectedImg ? 'pointer' : 'not-allowed' }}
          onClick={() => selectedImg && setShowShapeSelector(true)}
          disabled={!selectedImg}
        >
          Select Shape
        </button>
        <SaveWall onSave={saveWall} onDelete={handleDeleteSelected} onUpload={handleImageUpload} />
      </div>
    </div>
  );
};

export default Wall;
