import React, { useState } from 'react';
import { ChromePicker } from 'react-color';
import { Rnd } from 'react-rnd';
import './Wall.css';

const Wall = () => {
  // State for wall background color
  const [bgColor, setBgColor] = useState('#ffffff');

  // Actual wall size used to render
  const [wallSize, setWallSize] = useState({ width: 600, height: 400 });

  // Temporary inputs for wall size (so we apply only on button click)
  const [tempSize, setTempSize] = useState({ width: 600, height: 400 });

  // Uploaded image state
  const [images, setImages] = useState([]);

  // State to show/hide color picker
  const [showColorPicker, setShowColorPicker] = useState(false);

  // State for context menu
  const [contextMenu, setContextMenu] = useState({ visible: false, x: 0, y: 0, imgId: null });

  // Handle image file input
  // âœ… Fix: Handle multiple image uploads
  const handleImageUpload = (e) => {
    const files = Array.from(e.target.files);

    const newImages = files.map((file, index) => ({
      id: Date.now() + index, // unique ID
      src: URL.createObjectURL(file),
      x: 0,
      y: 0,
      width: 150,
      height: 150,
    }));

    setImages((prev) => [...prev, ...newImages]);
  };

  // Apply temporary width & height to actual wall
  const applySize = () => {
    setWallSize({
      width: +tempSize.width,
      height: +tempSize.height,
    });
  };

  // Handle right-click on image
  const handleContextMenu = (e, imgId) => {
    e.preventDefault();
    setContextMenu({ visible: true, x: e.clientX, y: e.clientY, imgId });
  };

  // Handle delete image
  const handleDeleteImage = () => {
    setImages((prev) => prev.filter((img) => img.id !== contextMenu.imgId));
    setContextMenu({ ...contextMenu, visible: false });
  };

  // Hide context menu on click elsewhere
  React.useEffect(() => {
    const handleClick = () => setContextMenu((cm) => ({ ...cm, visible: false }));
    if (contextMenu.visible) {
      window.addEventListener('click', handleClick);
    }
    return () => window.removeEventListener('click', handleClick);
  }, [contextMenu.visible]);

  return (
    <div className="main-layout">
      {/* Wall Section - placed on the left */}
      <div
        className="custom-wall"
        style={{
          backgroundColor: bgColor,
          width: wallSize.width + 'px', // Fixed template string usage
          height: wallSize.height + 'px',
          position: 'relative',
        }}
      >
        {/* Display uploaded image if present */}
        {images.map((img) => (
          <Rnd
            key={img.id}
            default={{
              x: img.x,
              y: img.y,
              width: img.width,
              height: img.height,
            }}
            bounds="parent"
            minWidth={50}
            minHeight={50}
          >
            <img
              src={img.src}
              alt="user-img"
              style={{
                width: '100%',
                height: '100%',
                objectFit: 'cover',
                border: '2px solid #000',
              }}
              onContextMenu={(e) => handleContextMenu(e, img.id)}
            />
          </Rnd>
        ))}

        {/* Context Menu for Delete Option */}
        {contextMenu.visible && (
          <div
            style={{
              position: 'fixed',
              top: contextMenu.y,
              left: contextMenu.x,
              background: '#fff',
              border: '1px solid #ccc',
              zIndex: 1000,
              boxShadow: '0 2px 8px rgba(0,0,0,0.15)',
              padding: '4px 0',
              minWidth: 100,
              cursor: 'pointer',
            }}
            onClick={handleDeleteImage}
          >
            <div style={{ padding: '8px 16px' }}>Delete</div>
          </div>
        )}
      </div>

      {/* Controls Section - placed on the right */}
      <div className="controls">

        {/* Wall size control inputs */}
        <div className="size-control">
          <label>
            Width:
            <input
              type="number"
              value={tempSize.width}
              onChange={(e) => setTempSize({ ...tempSize, width: e.target.value })}
            />
          </label>
          <label>
            Height:
            <input
              type="number"
              value={tempSize.height}
              onChange={(e) => setTempSize({ ...tempSize, height: e.target.value })}
            />
          </label>
          <button onClick={applySize}>Set Size</button> {/* Applies size to wall */}
        </div>

        {/* Toggle Color Picker button */}
        <button onClick={() => setShowColorPicker(!showColorPicker)}>
          {showColorPicker ? 'Hide Color Picker' : 'Choose Wall Color'}
        </button>

        {/* Conditional rendering of ChromePicker */}
        {showColorPicker && (
          <ChromePicker
            color={bgColor}
            onChange={(color) => setBgColor(color.hex)} // Update wall background
            disableAlpha
          />
        )}

        {/* Upload Image Button */}
        <button onClick={() => document.getElementById('uploadInput').click()}>
          Upload Image
        </button>

        {/* Hidden file input - triggered by button */}
        <input
          type="file"
          id="uploadInput"
          accept="image/*"
          multiple
          onChange={handleImageUpload}
          style={{ display: 'none' }}
        />
      </div>
    </div>
  );
};

export default Wall;
