import React, { useState } from 'react';

const defaultWalls = [
  '/walls/wall1.jpg',
  '/walls/wall2.jpg',
  '/walls/wall3.jpg',
  '/walls/wall4.jpg',
  '/walls/wall5.jpg',
  '/walls/wall6.jpg',
];

const WallPosterSelect = ({ onContinue }) => {
  const [selectedType, setSelectedType] = useState('');
  const [selectedWall, setSelectedWall] = useState(null);
  const [selectedColor, setSelectedColor] = useState('#ffffff');
  const [uploadedWall, setUploadedWall] = useState(null);

  const handleWallClick = (wall) => {
    setSelectedType('image');
    setSelectedWall(wall);
    setUploadedWall(null);
  };

  const handleColorChange = (e) => {
    setSelectedType('color');
    setSelectedColor(e.target.value);
    setSelectedWall(null);
    setUploadedWall(null);
  };

  const handleUpload = (e) => {
    const file = e.target.files[0];
    if (file) {
      setSelectedType('upload');
      setUploadedWall(URL.createObjectURL(file));
      setSelectedWall(null);
    }
  };

  const handleContinue = () => {
    if (selectedType === 'image') onContinue({ type: 'image', value: selectedWall });
    else if (selectedType === 'color') onContinue({ type: 'color', value: selectedColor });
    else if (selectedType === 'upload') onContinue({ type: 'upload', value: uploadedWall });
  };

  return (
    <div style={{ padding: 32, textAlign: 'center' }}>
      <h1>Wall Poster</h1>
      <h3>Choose a wall background</h3>
      <div style={{ display: 'flex', justifyContent: 'center', gap: 16, flexWrap: 'wrap', marginBottom: 24 }}>
        {defaultWalls.map((wall, idx) => (
          <img
            key={wall}
            src={wall}
            alt={`Wall ${idx + 1}`}
            style={{
              width: 100,
              height: 70,
              objectFit: 'cover',
              border: selectedWall === wall ? '3px solid #2a509c' : '2px solid #ccc',
              borderRadius: 8,
              cursor: 'pointer',
            }}
            onClick={() => handleWallClick(wall)}
          />
        ))}
      </div>
      <div style={{ marginBottom: 24 }}>
        <label style={{ marginRight: 12 }}>
          <input
            type="color"
            value={selectedColor}
            onChange={handleColorChange}
            style={{ width: 40, height: 40, border: 'none', background: 'none', cursor: 'pointer' }}
          />
          <span style={{ marginLeft: 8 }}>Choose background color</span>
        </label>
      </div>
      <div style={{ marginBottom: 24 }}>
        <input type="file" accept="image/*" onChange={handleUpload} />
        {uploadedWall && (
          <img src={uploadedWall} alt="Uploaded Wall" style={{ width: 100, height: 70, objectFit: 'cover', borderRadius: 8, marginLeft: 16 }} />
        )}
      </div>
      <button style={{ background: '#2a509c', color: 'white', fontWeight: 'bold', padding: '10px 24px', borderRadius: 6, fontSize: 18 }} disabled={!selectedType} onClick={handleContinue}>
        Continue
      </button>
    </div>
  );
};

export default WallPosterSelect;
